#!/usr/bin/python
# retroprocessing pour toutes les zones
# entre 2 dates

import datetime

import hotvolc_realtime

date1 = '201605260800'
date2 = '201605260945                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   '
debut = datetime.datetime.strptime(date1, '%Y%m%d%H%M')
fin = datetime.datetime.strptime(date2, '%Y%m%d%H%M')
step = datetime.timedelta(minutes=15)
i = 0
while debut <= fin:
    print
    debut.strftime('%Y%m%d%H%M')
    hotvolc_realtime.hotvolc_realtime(debut.strftime('%Y%m%d%H%M'), True)
    debut += step
    i += 1

print
i
